import React, { Component } from 'react'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import $ from 'jquery'
import './css/style.css'
import Footer from '../components/Footer'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';



class BlogContent extends Component {


  render() {
    console.log(this.props);

    let blog_des = this.props.location.state.blog_content
    console.log("inside render")

    return (
             <>

        <section class="promotion-wrap margin-top-max margin-bottom-max"><div class="container"><div class="text-center"><h2 class=" color">
          Promote you service -<span class="cms">with marketing!</span></h2><p><span class="subtitle">
            Marketing - awesome multifunctional landing page. Increase
            your conversion rate
                </span></p></div><div class="img-wrap"><img class="img-responsive" src="https://stag-images.contentstack.io/v3/assets/blt74a459c57a383bbf/blt0d83ea48a1584c8e/5e0049853b99960dea630dd8/promotion.png" alt=""/></div><div class="row"><div class="col-md-offset-2 col-md-8 text-justify"><p></p><p>
            Landing Page enables your visitors to get what they came for.
            Determine a target action you would like a user to take and
            make use of our suggested tools. You can ask a user to sign
            up, leave an email, choose a pricing plan or redirect him to
            another page. Use Flexy to increase your conversion rate. Do
            A/B testing. Use various suggested modules from Flexy theme to
            create different landing pages. The page with more conversions
            (more people taking action) wins.
                </p><p></p><div class="row"><div class="col-sm-4 "><a href="#" class="icons-links"><div class="icons-img-cont"><img class="img-responsive" src="https://stag-images.contentstack.io/v3/assets/blt74a459c57a383bbf/blt0d587d05ffe1fc47/5e004a503b99960dea630de8/apple-color.jpg" alt=""/><img class="img-responsive" src="https://stag-images.contentstack.io/v3/assets/blt74a459c57a383bbf/bltde6d3c060eebc7ce/5e004a4b3b99960dea630de2/apple.jpg" alt=""/></div><h4>App Store</h4></a></div><div class="col-sm-4"><a href="#" class="icons-links"><div class="icons-img-cont"><img class="img-responsive" src="https://stag-images.contentstack.io/v3/assets/blt74a459c57a383bbf/blt833bb491a4708974/5e004a730dd4f00ddf7a7d24/play-color.jpg" alt=""/><img class="img-responsive" src="https://stag-images.contentstack.io/v3/assets/blt74a459c57a383bbf/blt0b422651bea31dd3/5e004a6c9d54550df1186355/play.jpg" alt=""/></div><h4>Google Play</h4></a></div><div class="col-sm-4"><a href="#" class="icons-links"><div class="icons-img-cont"><img class="img-responsive" src="https://stag-images.contentstack.io/v3/assets/blt74a459c57a383bbf/blt8c342cfb600549bd/5e004b805590ff0e003d9123/win-color.jpg" alt=""/><img class="img-responsive" src="https://stag-images.contentstack.io/v3/assets/blt74a459c57a383bbf/blt518ae0c09e9bce98/5e004b7b0dd4f00ddf7a7d40/win.jpg" alt=""/></div><h4>Win Store</h4></a></div></div></div></div></div></section>

            </>
              )
          }
      }

      export default BlogContent



