import React, { Component } from "react";
import '../components/css/font-awesome.css'
import axios from 'axios'

class Footer extends Component {

  constructor(props) {
    super(props)

    this.state = {
       footer:""
    }
  }



  componentDidMount(){


    const home = axios.create({
      baseURL:
        "https://stag-new-api.contentstack.io/v3/content_types/footer/entries/bltf77a516ecef5ed3e",
      // timeout: 1000,
      headers: {
        api_key: "blt74a459c57a383bbf",
        authtoken: "bltac1fe73d78f7ddab"
      }
    });
    home.get()
      .then((data) => {
        console.log(data);
        this.setState({
          footer: data
        }, () => {
          console.log(this.state.nav)

        })
      }).catch((err) => {
        console.log(err);
      })
  }



  render() {
    return (
      <footer class="footer">
        <div class="footer-content">
          <div class="container">
            <div class="row">
              <div class="col-sm-6 col-md-4">
                <h5>Marketing in social</h5>{" "}
                <p>
                  <a href="#" class="footer-social">
                    <i class="fa fa-facebook-f"></i>Facebook
                  </a>
                </p>
                <p>
                  <a href="#" class="footer-social">
                    <i class="fa fa-twitter-square"></i>Twitter
                  </a>
                </p>
              </div>
              <div class="col-sm-6 col-md-4">
                <h5>Contact Info</h5>{" "}
                <address>
                  Marketing Plaza
                  <br />
                  San Francisco, CA
                  <p>
                    <a href="tel:+1.000.000.0000">Phone: +1.000.000.0000</a>
                  </p>
                  <p>
                    <a href="mailto:info@marketing.com">info@marketing.com</a>
                  </p>
                </address>
              </div>
              <div class="col-sm-6 col-md-4">
                <h5>About Marketing</h5>{" "}
                <p>
                  Marketing - awesome multifunctional landing page. Increase
                  your conversion rate. Increase your gain&nbsp;&nbsp;
                </p>
              </div>
            </div>
          </div>
        </div>
        <div class="footer-copyright">
          <span>
            <p>
              <a href="mailto:info@marketing.com">info@marketing.com </a>|
              Copyright Â© 2007-2016 Built.io. All Rights Reserved.
            </p>
          </span>
        </div>
      </footer>
    );
  }
}

export default Footer;
