import React, { Component } from 'react'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import $ from 'jquery'
import './css/style.css'
import Footer from '../components/Footer'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';



class BlogContent extends Component {


  render() {
    console.log(this.props);

    let blog_des = this.props.location.state.blog_content
    console.log("inside render")

    return (
      <>

        <section class="promotion-wrap margin-top-max margin-bottom-max">
          <div class="container">
            <div class="text-center">
              <h2 class=" color">
                <span class="cms">{this.props.location.state.blog_title}
                </span>
                </h2>
                </div>

          <div class="img-wrap">
            <img
              class="img-responsive"
              src={this.props.location.state.media.url}
              alt=""
            /></div>
            <div class="row"><div class="col-md-offset-2 col-md-8 text-justify"><p></p><p>
              Landing Page enables your visitors to get what they came for.
              Determine a target action you would like a user to take and
              make use of our suggested tools. You can ask a user to sign
              up, leave an email, choose a pricing plan or redirect him to
              another page. Use Flexy to increase your conversion rate. Do
              A/B testing. Use various suggested modules from Flexy theme to
              create different landing pages. The page with more conversions
              (more people taking action) wins.
                </p><p></p></div></div></div></section>

      </>
    )
  }
}

export default BlogContent



