// import React from 'react';
import React, { Component } from "react";
import About from './components/About'
import Product from './components/Product'
import Features from './components/Features'
import Home from './components/Home'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Modal from "react-responsive-modal";
import $ from "jquery";
import './components/css/toggle.css'
import './components/css/style.css'
import axios from 'axios'




class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
      sign: false,
      login: false,
      nav:"",
    }
  }


  componentDidMount() {
    const header = axios.create({
      baseURL:
        "https://stag-new-api.contentstack.io/v3/content_types/header/entries/bltfd380d3ea73df6c8",
      // timeout: 1000,
      headers: {
        api_key: "blt74a459c57a383bbf",
        authtoken: "bltac1fe73d78f7ddab"
      }
    });
    header.get()
    .then((data) => {
      console.log(data);
      this.setState({
        nav: data
      }, () => {
        console.log(this.state.nav)
      })
    }).catch((err) => {
      console.log(err);
    })

    var $header = $('.header-animated');
    var $headers = $('.header-animated opaque');
    var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
    var $logoDefault = $header.find('.logo > img').data('logo-default'); // black logo
    $(".header").css("display", "block")

    if (window.location.pathname == "/features" || window.location.pathname == "/about") {
      $(".header").addClass("opaque");
      $header.find('.logo > img').attr('src', $logoDefault);
    }



    // video Stop 

    // navbar toggle
    $('.nav-toggle').click(function () {
      $(this).toggleClass('open');
    });

    // navbar dropdown
    $(".dropdown").hover(function () {
      $(".dropdown").addClass("open")
    }, function () {
      $(".dropdown").removeClass("open")
    })

    $('#sign_up').click(function () {
      window.location.reload(true);
    });
    $('#login_btn').click(function () {
      window.location.reload(true);
    });

    $(window).on('scroll', function () {
      if (window.location.pathname !== "/features" && window.location.pathname !== "/about") {
        // console.log('true');
        if ($(window).scrollTop() > 100) {
          $header.fadeIn().addClass('opaque');
          $header.find('.logo > img').attr('src', $logoDefault);

        } else {
          $header.removeClass('opaque');
          $header.find('.logo > img').attr('src', $logoAlt);
        }
      }
    });

  }



  onOpenModal = () => {
    this.setState({ sign: true });
  };

  onOpenModalLogin = () => {
    this.setState({ login: true });
  };

  onCloseModal = () => {
    this.setState({ sign: false });
  };

  onCloseModalclose = () => {
    this.setState({ login: false });
  };

  render() {
    const { login, sign } = this.state;
    console.log(this.state.nav)

    if(this.state.nav !== ""){

      return (
        <Router>
          <>
            <header className="header header-animated opaque" style={{ "display": 'block', "paddingTop": "5px", "paddingBottom": "5px" }}>
              <div className="container">
                <nav className="navbar navbar-default" role="navigation">
                  <div className="navbar-header">
                    <a className="logo" href="/">
                      <img className="img-responsive logo" src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/bltca8978c28f812283/58f0a8062cad9d980736e1d5/download" alt="" data-logo-alt="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/blt5d6e0efe5c5d2936/58f0a80478ac9beb071a07f0/download" data-logo-default="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/bltca8978c28f812283/58f0a8062cad9d980736e1d5/download" />
                    </a>
                  </div>


                  <div className="nav-toggle collapsed" data-toggle="collapse" data-target="#navbarMain" aria-expanded="false" style={{ "top": "15px" }}>
                    <span></span>
                    <span></span>
                    <span></span>
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                  {/* <!-- Collect the nav links, forms, and other content for toggling --> */}
                  <div className="navbar-collapse collapse in" id="navbarMain" aria-expanded="true" style={{ top: "65px" }}>

                    <ul className="nav navbar-nav navbar-right">
                      <li>
                        <button className="btn btn-primary-outline" id="signup" onClick={this.onOpenModal}>Signup</button>
                      </li>
                      <li>
                        <button className="btn btn-primary-outline" id="login" onClick={this.onOpenModalLogin}>Login</button>
                      </li>
                    </ul>
                    <ul className="nav navbar-nav collapsed-color">

                      <li>
                        <Link to={'/'} className="nav-link"> Home </Link>
                      </li>

                      <li>
                        <Link to={'/features'} className="nav-link">Features</Link>
                      </li>

                      <li>
                        <Link to={'/about'} className="nav-link"> About </Link>
                      </li>

                      <li>
                        <Link to={'/product'} className="nav-link"> Product </Link>
                      </li>

                    </ul>

                  </div>

                  {/* <!-- .navbar-collapse --> */}
                </nav>
              </div>

            </header>
            {/* Sign up model */}

            <Modal open={sign} onClose={this.onCloseModal}>
              <div className="modal-body">
                <h2>Get Started Absolutely<span> Free!</span></h2>
                <span className="subtitle">No credit card needed</span>
                <form className="contact-form form-validate3" novalidate="novalidate">
                  <div className="form-group">
                    <input className="form-control" type="text" name="name" id="name" placeholder="First Name" required="" autocomplete="off" aria-required="true" />
                  </div>
                  <div className="form-group">
                    <input className="form-control" type="email" name="email" placeholder="E-mail" required="" autocomplete="off" aria-required="true" />
                  </div>
                  <div className="form-group">
                    <input type="password" name="pass" className="form-control" placeholder="Password" required="" autocomplete="off" aria-required="true" />
                  </div>
                  <input className="btn btn-md btn-primary btn-center" id="sign_up" type="button" value="Sign Up" />
                </form>
              </div>
            </Modal>

            {/* <!-- signUp End -->
          <!-- login --> */}

            <Modal open={login} onClose={this.onCloseModalclose}>
              <div className="modal-body">
                <h2>Login and Get <span>Started</span></h2>
                <span className="subtitle">Just fill in the form below</span>
                <form className="contact-form form-validate4" novalidate="novalidate">
                  <div className="form-group">
                    <input className="form-control" type="email" name="email" placeholder="E-mail" required="" autocomplete="off" aria-required="true" />
                  </div>
                  <div className="form-group">
                    <input type="password" name="pass" className="form-control" placeholder="Password" required="" autocomplete="off" aria-required="true" />
                  </div>
                  <input className="btn btn-md btn-primary btn-center" id="login_btn" type="button" value="Login" />
                </form>
              </div>
            </Modal>
          </>
          <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/features" component={Features} />
            <Route path="/about" component={About} />
            <Route path="/product" component={Product} />
          </Switch>
        </Router>

      );
    }
        else{

          return <div>{null}</div>;
        }

    }
  }





export default App;
