import React, { Component } from 'react'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import $ from 'jquery'
import './css/style.css'


 class BlogContent extends Component {

    componentDidMount(){
        console.log(this.props);
        var $header = $('.header-animated');
        var $headers = $('.header-animated opaque');
        var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
        var $logoDefault = $header.find('.logo > img').data('logo-default'); // black logo
        $(".header").css("display", "block")

        if (window.location.pathname == "/features" || window.location.pathname == "/about" || window.location.pathname == `${/blog/}`) {
          $(".header").addClass("opaque");
          $header.find('.logo > img').attr('src', $logoDefault);
        }
        else {
          $(".header").removeClass("opaque")
        }

    }
    render() {
        // let val =this.props
        // console.log(val);
        console.log(this.props);
        let main_Content=this.props.blog_content

        return (
            <div>

                <h1>{this.props.title}</h1>
                {ReactHtmlParser (main_Content)}
                <div class="col-md-8">
                    <img
                      class="img-responsive"
                      src={this.props.media.url}
                      alt=""
                    />
                  </div>

            </div>
        )
    }
}

export default BlogContent
