import React, { Component } from 'react'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import $ from 'jquery'
import './css/style.css'
import Footer from '../components/Footer'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import './css/toggle.css'




class BlogContent extends Component {


  render() {
    console.log(this.props);

    let blog_des = this.props.location.state.blog_content
    console.log("inside render")

    return (
      <>
        <section class="promotion-wrap margin-top-max margin-bottom-max">
          <div class="text-center">
            <h2 class=" color"><span
           > {this.props.location.state.blog_title}

                </span></h2>

          </div>
          <div class="img-wrap"><img class="img-responsive" id="center" src={this.props.location.state.media.url} alt="" /></div>

          <div class="col-md-offset-2 col-md-8 text-justify"><p></p>
            {ReactHtmlParser(blog_des)}
            <Link to={"/blog"}>Return back...</Link>
            <p></p>
          </div>
        </section>
        <div class="widget-before-footer widget-area"><div class="wrap"><section id="custom_html-16" class="widget_text widget-odd widget-last widget-first widget-1 widget widget_custom_html"><div class="widget_text widget-wrap"><h4 class="widget-title widgettitle">Ready to start a blog?</h4>
<div class="textwidget custom-html-widget"><p style="text-align: center; font-size: 22px;">Start a blog in 3 simple steps with our video tutorial.</p>
<p style="text-align: center;"><a target="_blank" href="https://georgialoustudios.com/how-to-start-a-blog/" rel="noopener noreferrer"><button>
	I want to start a blog!
	</button></a></p></div></div></section>
</div></div>
      <Footer/>
      </>
    )
  }
}

export default BlogContent



