import React, { Component } from 'react'
import dataSource from './DataSource'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import BlogContent from './BlogContent' 
import "../components/css/style.css";
import $ from 'jquery'




class Blog extends Component {

    componentDidMount(){
        var $header = $('.header-animated');
        var $headers = $('.header-animated opaque');
        var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
        var $logoDefault = $header.find('.logo > img').data('logo-default'); // black logo
        $(".header").css("display", "block")

        if (window.location.pathname == "/features" || window.location.pathname == "/about" || window.location.pathname == "/blog") {
          $(".header").addClass("opaque");
          $header.find('.logo > img').attr('src', $logoDefault);
        }
        else {
          $(".header").removeClass("opaque")
        }
    }
    render() {
        return (
            <div style={""}>
                {
                    dataSource.map((item, index) => {
                        return (
                            <>
                            <Router>
                               <Link to={`/blog${item.route}`}><h1>{item.title}</h1></Link>
                               <Switch>
                                   <Route exact path={`/blog${item.route}`} component={() => <BlogContent title={item.title} content={item.content} />}/>
                               </Switch>

                            </Router>
                            </>
                        )
                    })
                }
            </div>
        )
    }
}

export default Blog
