import React from "react";
import { render } from "react-dom";
import Modal from "react-responsive-modal";

const styles = {
  fontFamily: "sans-serif",
  textAlign: "center"
};

class Modals extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            open: false
        }
    }



  onOpenModal = () => {
    this.setState({ open: true });
  };

  onCloseModal = () => {
    this.setState({ open: false });
  };

  render() {
    const { open } = this.state;
    return (
      <div style={styles}>
        <h2>react-responsive-modal</h2>
        <button onClick={this.onOpenModal}>Open modal</button>
        <Modal open={open} onClose={this.onCloseModal}>
          {/* <h2>Simple centered modal</h2> */}
          <div class="modal-body">
                <h2>Get Started Absolutely<span> Free!</span></h2>
                <span class="subtitle">No credit card needed</span>
                <form class="contact-form form-validate3" novalidate="novalidate">
                    <div class="form-group">
                        <input class="form-control" type="text" name="name" id="name" placeholder="First Name" required="" autocomplete="off" aria-required="true"/>
                    </div>
                    <div class="form-group">
                        <input class="form-control" type="email" name="email" placeholder="E-mail" required="" autocomplete="off" aria-required="true"/>
                    </div>
                    <div class="form-group">
                        <input type="password" name="pass" class="form-control" placeholder="Password" required="" autocomplete="off" aria-required="true"/>
                    </div>
                    <input class="btn btn-md btn-primary btn-center" id="sign_up" type="button" value="Sign Up"/>
                </form>
            </div>
        </Modal>
      </div>
    );
  }
}

export default Modals
