import React, { Component } from 'react'
// import Header from './Header'
import Typed from 'react-typed';
import Footer from './Footer'
import $ from "jquery";
import './css/toggle.css'
import './css/style.css'
// import './css/style1.css'
import axios from 'axios'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';



class Home extends Component {

  constructor(props) {
    super(props)

    this.state = {
      home: ""
    }
  }


  componentDidMount() {


    const home = axios.create({
      baseURL:
        "https://stag-new-api.contentstack.io/v3/content_types/home/entries/blt1b748703c9be2fe0",
      // timeout: 1000,
      headers: {
        api_key: "blt74a459c57a383bbf",
        authtoken: "bltac1fe73d78f7ddab"
      }
    });
    home.get()
      .then((data) => {
        console.log(data);
        this.setState({
          home: data
        }, () => {
          console.log(this.state.nav)

        })
      }).catch((err) => {
        console.log(err);
      })

      $(document).ready(function() {
        var $header = $('.header-animated');
        var $headers = $('.header-animated opaque');
        var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
        var $logoDefault = $header.find('.logo > img').data('logo-default'); // black logo
        $(".header").css("display","block")       

        if (window.location.pathname == "/features" || window.location.pathname == "/about") {
           $(".header").addClass("opaque");
           $header.find('.logo > img').attr('src', $logoDefault);
        }

        if(window.location.pathname == "/" || window.location.pathname == '/product'){
          $(".header").addClass(".header-animated")
        } 

        $('#tabs a').click(function(e) {
            e.preventDefault()
            $(this).tab('show')
        })


        // video Stop 
        $('#videoModal').on('hidden.bs.modal', function() {
            $("#videoModal iframe").attr("src", $("#videoModal iframe").attr("src"));
        });

        // navbar toggle
        $('.nav-toggle').click(function() {
            $(this).toggleClass('open');
        });

        // navbar dropdown
        $(".dropdown").hover(function() {
            $(".dropdown").addClass("open")
        }, function() {
            $(".dropdown").removeClass("open")
        })


        $(window).on('scroll', function() {
            if (window.location.pathname != "/features" && window.location.pathname != "/about") {
                console.log('true');
                if ($(window).scrollTop() > 100) {
                    $header.fadeIn().addClass('opaque');
                    $header.find('.logo > img').attr('src', $logoDefault);

                } else {
                    $header.removeClass('opaque');
                    $header.find('.logo > img').attr('src', $logoAlt);
                }
            }
        });

        //var currentpath = window.location.pathname;
        //$('ul.nav li a').each(function(){
        //    var anchor_link = $(this).attr('href');
        //    if (currentpath == anchor_link){
        //        $(this).parent().addClass('active');
        //    }
        //});

        $('#sign_up').click(function(){
            window.location.reload(true);
        });
        $('#login_btn').click(function(){
            window.location.reload(true);
        });

        //$('form').submit(function(){
        //    location.reload(true);
        //});



        // Logo header change
        // $(window).on('load', function() {
        //     if ($(window).scrollTop() > 100) {
        //         var $logoAlt = $header.find('.logo > img').data('logo-default'); // white logo
        //         $header.find('.logo > img').attr('src', $logoAlt);
        //     } else if ($header.length > 0) {
        //         var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
        //         $header.find('.logo > img').attr('src', $logoAlt);
        //     }
        // });

        // smooth scroll
        // $('a').click(function(e) {
        //     console.log("okay")
        //     if ($(this).data('scrollto')) {
        //         $(this).attr('href', $(this).data('scrollto'))
        //         if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
        //             var target = $(this.hash);
        //             target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
        //             if (target.length) {
        //                 $('html, body').animate({
        //                     scrollTop: target.offset().top - 80
        //                 }, 1000);
        //                 return false;
        //             }
        //         }
        //     }
        // });
    });

  }



  render() {
    if (this.state.home !== "") {
      const html = this.state.home.data.entry.hero_banner.description;


      return (
        <>
          <section>
            <div class="wrapper">
              <div class="home-hero-bg hero-bg" id="home">
                <div class="background-overlay"></div>
                <div class="aligned-container typed-container">
                  <div class="container">
                    <div class="typing-block">
                      <div class="typing-block">
                        {this.state.home.data.entry.hero_banner.title} <span> <Typed strings={[`${this.state.home.data.entry.hero_banner.rolling_text[0]}`]} typeSpeed={50} />  </span>
                      </div>

                    </div>

                    {ReactHtmlParser(html)}
                    <a href="#" class="btn btn-primary mrm">{this.state.home.data.entry.hero_banner.cta[0].title}</a>
                    <a href="#" class="underline-text">{this.state.home.data.entry.hero_banner.cta[1].title}<i class="fa fa-angle-right"></i></a>
                  </div>
                </div>
              </div>
              <div class="main-container">
              </div>
            </div>
            {/* <!-- Video --> */}
            <div class="modal videoModal fade" id="videoModal" tabindex="-1" role="dialog" aria-hidden="true">
              <div class="modal-dialog modal-lg">
                <div class="modal-header">
                  <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">×</span></button>
                </div>
                <div class="modal-content">
                  <div class="modal-body">
                    <div class="embed-responsive embed-responsive-16by9" id="yt-player">

                      <img class="img-responsive" src="img/hero-bg.jpg" />
                      <iframe class="embed-responsive-item" src="http://player.vimeo.com/video/27408483?title=0&byline=0&portrait=0&color=ffffff"></iframe>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </section>
          <Footer />
        </>

      );
    }
    else if (this.state.home == "") {
      return (
        null
      )
    }
  }
}

export default Home
