import React, { Component } from 'react'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import $ from 'jquery'
import './css/style.css'


 class BlogContent extends Component {

    componentDidMount(){
        console.log(this.props);
        var $header = $('.header-animated');
        var $headers = $('.header-animated opaque');
        var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
        var $logoDefault = $header.find('.logo > img').data('logo-default'); // black logo
        $(".header").css("display", "block")
        console.log(window.location.pathname);
        // console.log(`/blog${this.props.url}`)
        if (window.location.pathname == "/features" || window.location.pathname == "/about" || window.location.pathname ==`/blog${this.props.url}`) {
          $(".header").addClass("opaque");
          $header.find('.logo > img').attr('src', $logoDefault);
        }
        else {
          $(".header").removeClass("opaque")
        }

    }
    render() {
        // let val =this.props
        console.log("BlogContent");
        console.log(this.props.history.location.state);
        console.log(this.props.history.location.state.blog_title);
        let main_Content=this.props.blog_content

        return (
            <div>
                <h1></h1>
                <h1>{this.props.history.location.state.blog_title}</h1>
                {ReactHtmlParser (main_Content)}
                <div class="col-md-8">
                    <img
                      class="img-responsive"
                      src={this.props.history.location.state.media.url}
                      alt=""
                    />
                  </div>

            </div>
        )
    }
}

export default BlogContent
