import React, { Component } from 'react'
import dataSource from './DataSource'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import BlogContent from './BlogContent'
import "./css/style.css";
import $ from 'jquery'
import { getData } from './Helper'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';






class Blog extends Component {

    constructor(props) {
        super(props)

        this.state = {
            blogPage: ""
        }
    }


    componentDidMount() {

        getData(process.env.REACT_APP_URL_BLOG)
            .then((data) => {
                // console.log(data);
                this.setState({
                    blogPage: data
                }, () => {
                    console.log(this.state.blogPage)

                })
            }).catch((err) => {
                console.log(err);
            })


    }
    render() {

        if (this.state.blogPage !== "") {
            return (
                <>
                    {
                        this.state.blogPage.data.entries.map((value) => {
                            return (
                                <>
                                    <Link to={`/blog${value.url}`}><h1>{value.title}</h1></Link>
                                    <Switch>
                                        {/* <Route exact path={`/blog${item.route}`} render={(i) => <BlogContent {...i} property={item} />} /> */}

                                        <Route exact path={`/blog${value.url}`} component={(i) => <BlogContent content={value.blog_content} title={value.title} image />} />
                                    </Switch>
                                </>
                            )

                        })
                    }
                </>

            )
        }
        else if (this.state.blogPage === "") {
            return (
                null
            )
        }
    }
}


export default Blog
