import React, { Component } from 'react'
import dataSource from './DataSource'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import BlogContent from './BlogContent'
import "./css/style.css";
import $ from 'jquery'
import { getData } from './Helper'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';






class Blog extends Component {

    constructor(props) {
        super(props)

        this.state = {
            blogPage: ""
        }
    }


    componentDidMount() {

        getData(process.env.REACT_APP_URL_BLOG)
            .then((data) => {
               // console.log(data);
                this.setState({
                    blogPage: data
                }, () => {
                    console.log(this.state.blogPage)

                })
            }).catch((err) => {
                console.log(err);
            })


    }
    render() {

        if (this.state.blogPage !== "") {
            return (
                <>
                {
                    this.state.blogPage.data.entries.map((link,title,body,image)=>{
                    <Link to={`/blog${link.url}`}><h1>{title.title}</h1></Link>                    
                })
                }
                </>

            )
        }
        else if (this.state.blogPage === "") {
            return (
                null
            )
        }
    }
}


export default Blog
