import React, { Component } from "react";
import "../components/css/style.css";
import Footer from './Footer'
import $ from 'jquery'
import axios from 'axios'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';




class About extends Component {

  constructor(props) {
    super(props)

    this.state = {
       aboutState:""
    }
  }


  componentDidMount() {

    const about = axios.create({
      baseURL:
        "https://stag-new-api.contentstack.io/v3/content_types/about_page/entries/bltdfa5118983a97b5a",
      // timeout: 1000,
      headers: {
        api_key: "blt74a459c57a383bbf",
        authtoken: "bltac1fe73d78f7ddab"
      }
    });
    about.get()
      .then((data) => {
        console.log(data);
        this.setState({
          aboutState: data
        }, () => {
          console.log(this.state.aboutState)

        })
      }).catch((err) => {
        console.log(err);
      })

    $(document).ready(function () {
      var $header = $('.header-animated');
      var $headers = $('.header-animated opaque');
      var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
      var $logoDefault = $header.find('.logo > img').data('logo-default'); // black logo
      $(".header").css("display", "block")

      if (window.location.pathname == "/features" || window.location.pathname == "/about") {
        $(".header").addClass("opaque");
        $header.find('.logo > img').attr('src', $logoDefault);
      }

      $('#tabs a').click(function (e) {
        e.preventDefault()
        $(this).tab('show')
      })



      // navbar toggle
      $('.nav-toggle').click(function () {
        $(this).toggleClass('open');
      });


      $(window).on('scroll', function () {
        if (window.location.pathname != "/features" && window.location.pathname != "/about") {
          // console.log('true');
          if ($(window).scrollTop() > 100) {
            $header.fadeIn().addClass('opaque');
            $header.find('.logo > img').attr('src', $logoDefault);

          } else {
            $header.removeClass('opaque');
            $header.find('.logo > img').attr('src', $logoAlt);
          }
        }
      });

      //var currentpath = window.location.pathname;
      //$('ul.nav li a').each(function(){
      //    var anchor_link = $(this).attr('href');
      //    if (currentpath == anchor_link){
      //        $(this).parent().addClass('active');
      //    }
      //});

      $('#sign_up').click(function () {
        window.location.reload(true);
      });
      $('#login_btn').click(function () {
        window.location.reload(true);
      });

      //$('form').submit(function(){
      //    location.reload(true);
      //});



      // Logo header change
      // $(window).on('load', function() {
      //     if ($(window).scrollTop() > 100) {
      //         var $logoAlt = $header.find('.logo > img').data('logo-default'); // white logo
      //         $header.find('.logo > img').attr('src', $logoAlt);
      //     } else if ($header.length > 0) {
      //         var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
      //         $header.find('.logo > img').attr('src', $logoAlt);
      //     }
      // });

      // smooth scroll
      // $('a').click(function(e) {
      //     console.log("okay")
      //     if ($(this).data('scrollto')) {
      //         $(this).attr('href', $(this).data('scrollto'))
      //         if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
      //             var target = $(this.hash);
      //             target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
      //             if (target.length) {
      //                 $('html, body').animate({
      //                     scrollTop: target.offset().top - 80
      //                 }, 1000);
      //                 return false;
      //             }
      //         }
      //     }
      // });
    });

  }

  render() {
    return (
      <>
        <div class="main-container">
          <section class="team-wrap">
            <div class="container">
              <div class="row">
                <div class="col-md-4 margin-top-50 margin-bottom">
                Re
                  <a href="#" class="btn btn-primary mrl">
                    See more
                </a>
                  <a href="#" class="btn btn-secondary">
                    See more
                </a>
                </div>
                <div class="col-md-8">
                  <img
                    class="img-responsive"
                    src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/bltcdb83375cb7bbf6f/58f0a7e86ef56ed707e15e54/download"
                    alt=""
                  />
                </div>
              </div>
            </div>
          </section>
          <section class="promotion-wrap margin-top-max margin-bottom-max">
            <div class="container">
              <div class="text-center">
                <h2 class=" color">
                  Promote you service -<span class="cms">with marketing!</span>
                </h2>
                <p>
                  <span class="subtitle">
                    Marketing - awesome multifunctional landing page. Increase
                    your conversion rate
                </span>
                </p>
              </div>
              <div class="img-wrap">
                <img
                  class="img-responsive"
                  src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/bltb7209a25a766e3f1/58f0a7f65fb5e2e107e95255/download"
                  alt=""
                />
              </div>
              <div class="row">
                <div class="col-md-offset-2 col-md-8 text-justify">
                  <p></p>
                  <p>
                    Landing Page enables your visitors to get what they came for.
                    Determine a target action you would like a user to take and
                    make use of our suggested tools. You can ask a user to sign
                    up, leave an email, choose a pricing plan or redirect him to
                    another page. Use Flexy to increase your conversion rate. Do
                    A/B testing. Use various suggested modules from Flexy theme to
                    create different landing pages. The page with more conversions
                    (more people taking action) wins.
                </p>
                  <p></p>
                  <div class="row">
                    <div class="col-sm-4 ">
                      <a href="#" class="icons-links">
                        <div class="icons-img-cont">
                          <img
                            class="img-responsive"
                            src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/blt4e93d6f55a328202/58f0a7e2705003a707788797/download"
                            alt=""
                          />
                          <img
                            class="img-responsive"
                            src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/blt9aac61479488804b/58f0a7e424a305ac07118f54/download"
                            alt=""
                          />
                        </div>
                        <h4>App Store</h4>
                      </a>
                    </div>
                    <div class="col-sm-4">
                      <a href="#" class="icons-links">
                        <div class="icons-img-cont">
                          <img
                            class="img-responsive"
                            src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/blt13b7a651df0efeb7/58f0a7df1bf84fb1075a5019/download"
                            alt=""
                          />
                          <img
                            class="img-responsive"
                            src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/blt7180525af793df86/58f0a7e0efa4b9e607ceb797/download"
                            alt=""
                          />
                        </div>
                        <h4>Google Play</h4>
                      </a>
                    </div>
                    <div class="col-sm-4">
                      <a href="#" class="icons-links">
                        <div class="icons-img-cont">
                          <img
                            class="img-responsive"
                            src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/bltccafb0806a363570/58f0a7d95e0befa207c40b3a/download"
                            alt=""
                          />
                          <img
                            class="img-responsive"
                            src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/blt339ceda743394f78/58f0a7dc24a305ac07118f50/download"
                            alt=""
                          />
                        </div>
                        <h4>Win Store</h4>
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </section>
          <div
            class="blockquote-wrap"
            style={{ "background-image": "url(https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/blt51a91a2b16e31a5b/58f0a7c05fb5e2e107e9524d/download)" }}>
            <div class="background-overlay"></div>
            <div class="container">
              <blockquote>
                <p></p>
                <p>
                  If you change the way you look at things, the things you look at
                  will change
              </p>
                <p></p>
                <p class="speaker">- Someone Unknown</p>
              </blockquote>
            </div>
          </div>
        </div>
        <Footer />
      </>
    );
  }
}

export default About;
