import React, { Component } from 'react'
import dataSource from './DataSource'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import BlogContent from './BlogContent'
import "./css/style.css";
import $ from 'jquery'
import { getData } from './Helper'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';






class Blog extends Component {

    constructor(props) {
        super(props)

        this.state = {
            blogPage: ""
        }
    }


    componentDidMount() {

        getData(process.env.REACT_APP_URL_BLOG)
            .then((data) => {
                 console.log(data);
                this.setState({
                    blogPage: data
                }, () => {
                    console.log(this.state.blogPage)

                })
            }).catch((err) => {
                console.log(err);
            })


    }
    render() {

        // if (this.state.blogPage !== "") {
            return (

                <>
                    <Router>

                        <section class="margin-top-max margin-bottom-max">
                            <div class="container">

                                <div class="row">
                                    <div class="col-md-6 text-left padding-right-max">
                                        <h4>
                                            <strong>{this.state.blogPage.data.entry.real_data.real_data_content[0].title}</strong>
                                        </h4>
                                        <p class="mbl">
                                            {this.state.productState.data.entry.real_data.real_data_content[0].description}
                                        </p>

                                        <h4>
                                            <strong>{this.state.productState.data.entry.real_data.real_data_content[1].title}</strong>
                                        </h4>
                                        <p class="mbl">
                                            {this.state.productState.data.entry.real_data.real_data_content[1].description}
                                        </p>

                                        <h4>
                                            <strong>{this.state.productState.data.entry.real_data.real_data_content[2].title}</strong>
                                        </h4>
                                        <p class="mbl">
                                            {this.state.productState.data.entry.real_data.real_data_content[2].description}

                                        </p>
                                    </div>

                                    <div class="col-md-6">
                                        <img
                                            class="img-responsive"
                                            src={this.state.productState.data.entry.real_data.image.url}
                                            alt=""
                                        />
                                    </div>
                                </div>
                            </div>
                        </section>
                    </Router>
                </>

            )
        }
        // else if (this.state.home === "") {
        //     return (
        //         null
        //     )
        // }
    }


export default Blog
