// import React from 'react';
import React, { Component } from "react";
import About from './components/About'
import Product from './components/Product'
import Features from './components/Features'
import Home from './components/Home'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Modal from "react-responsive-modal";
import $ from "jquery";
import './components/css/toggle.css'
import './components/css/style.css'








class App extends Component {

  componentDidMount() {

    $(document).ready(function () {
      var $header = $('.header-animated');
      var $headers = $('.header-animated opaque');
      var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
      var $logoDefault = $header.find('.logo > img').data('logo-default'); // black logo
      $(".header").css("display", "block")

      if (window.location.pathname == "/features" || window.location.pathname == "/about") {
        $(".header").addClass("opaque");
        $header.find('.logo > img').attr('src', $logoDefault);
      }

      $('#tabs a').click(function (e) {
        e.preventDefault()
        $(this).tab('show')
      })

      // video Stop 
      $('#videoModal').on('hidden.bs.modal', function () {
        $("#videoModal iframe").attr("src", $("#videoModal iframe").attr("src"));
      });

      // navbar toggle
      $('.nav-toggle').click(function () {
        $(this).toggleClass('open');
      });

      // navbar dropdown
      $(".dropdown").hover(function () {
        $(".dropdown").addClass("open")
      }, function () {
        $(".dropdown").removeClass("open")
      })

      // typing Effect 
      var stringArray = window.entry ? window.entry.hero_banner.rolling_text : null

      $(".typed").typed({
        strings: stringArray,
        typeSpeed: 30, // typing speed
        backDelay: 500, // pause before backspacing
        loop: true, // loop on or off (true or false)
        loopCount: false, // number of loops, false = infinite
        callback: function () { } // call function after typing is done
      });

      $(window).on('scroll', function () {
        if (window.location.pathname != "/features" && window.location.pathname != "/about") {
          console.log('true');
          if ($(window).scrollTop() > 100) {
            $header.fadeIn().addClass('opaque');
            $header.find('.logo > img').attr('src', $logoDefault);

          } else {
            $header.removeClass('opaque');
            $header.find('.logo > img').attr('src', $logoAlt);
          }
        }
      });

      //var currentpath = window.location.pathname;
      //$('ul.nav li a').each(function(){
      //    var anchor_link = $(this).attr('href');
      //    if (currentpath == anchor_link){
      //        $(this).parent().addClass('active');
      //    }
      //});

      $('#sign_up').click(function () {
        windowlocation.reload(true);
      });
      $('#login_btn').click(function () {
        location.reload(true);
      });

      //$('form').submit(function(){
      //    location.reload(true);
      //});



      // Logo header change
      // $(window).on('load', function() {
      //     if ($(window).scrollTop() > 100) {
      //         var $logoAlt = $header.find('.logo > img').data('logo-default'); // white logo
      //         $header.find('.logo > img').attr('src', $logoAlt);
      //     } else if ($header.length > 0) {
      //         var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
      //         $header.find('.logo > img').attr('src', $logoAlt);
      //     }
      // });

      // smooth scroll
      // $('a').click(function(e) {
      //     console.log("okay")
      //     if ($(this).data('scrollto')) {
      //         $(this).attr('href', $(this).data('scrollto'))
      //         if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
      //             var target = $(this.hash);
      //             target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
      //             if (target.length) {
      //                 $('html, body').animate({
      //                     scrollTop: target.offset().top - 80
      //                 }, 1000);
      //                 return false;
      //             }
      //         }
      //     }
      // });
    });

  }

  constructor(props) {
    super(props)

    this.state = {
      sign: false,
      login: false
    }
  }

  onOpenModal = () => {
    this.setState({ sign: true });
  };

  onOpenModalLogin = () => {
    this.setState({ login: true });
  };

  onCloseModal = () => {
    this.setState({ sign: false });
  };

  onCloseModalclose = () => {
    this.setState({ login: false });
  };

  render() {
    const { login, sign } = this.state;

    return (
      <Router>
        <>
          <header class="header header-animated opaque" style={{ display: 'block' }}>
            <div class="container">
              <nav class="navbar navbar-default" role="navigation">


                <div class="nav-toggle collapsed" data-toggle="collapse" data-target="#navbarMain" aria-expanded="false">
                  <span></span>
                  <span></span>
                  <span></span>
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
                {/* <!-- Collect the nav links, forms, and other content for toggling --> */}
                <div class="navbar-collapse collapse in" id="navbarMain" aria-expanded="true" >
                  <div class="navbar-header">
                    <a class="logo" href="/">
                      <img class="img-responsive logo" src="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/bltca8978c28f812283/58f0a8062cad9d980736e1d5/download" alt="" data-logo-alt="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/blt5d6e0efe5c5d2936/58f0a80478ac9beb071a07f0/download" data-logo-default="https://images.contentstack.io/v3/assets/blt2f8208e2d15beeb0/bltca8978c28f812283/58f0a8062cad9d980736e1d5/download" />
                    </a>
                  </div>
                  <ul class="nav navbar-nav navbar-right">
                    <li>
                      <button class="btn btn-primary-outline" id="signup" onClick={this.onOpenModal}>Signup</button>
                    </li>
                    <li>
                      <button class="btn btn-primary-outline" id="login" onClick={this.onOpenModalLogin}>Login</button>
                    </li>
                  </ul>
                  <ul class="nav navbar-nav collapsed-color">

                    <li>
                      <Link to={'/'} className="nav-link"> Home </Link>
                    </li>

                    <li>
                      <Link to={'/features'} className="nav-link">Features</Link>
                    </li>

                    <li>
                      <Link to={'/about'} className="nav-link"> About </Link>
                    </li>

                    <li>
                      <Link to={'/product'} className="nav-link"> Product </Link>
                    </li>

                  </ul>

                </div>

                {/* <!-- .navbar-collapse --> */}
              </nav>
            </div>

          </header>
          {/* Sign up model */}

          <Modal open={sign} onClose={this.onCloseModal}>
            <div class="modal-body">
              <h2>Get Started Absolutely<span> Free!</span></h2>
              <span class="subtitle">No credit card needed</span>
              <form class="contact-form form-validate3" novalidate="novalidate">
                <div class="form-group">
                  <input class="form-control" type="text" name="name" id="name" placeholder="First Name" required="" autocomplete="off" aria-required="true" />
                </div>
                <div class="form-group">
                  <input class="form-control" type="email" name="email" placeholder="E-mail" required="" autocomplete="off" aria-required="true" />
                </div>
                <div class="form-group">
                  <input type="password" name="pass" class="form-control" placeholder="Password" required="" autocomplete="off" aria-required="true" />
                </div>
                <input class="btn btn-md btn-primary btn-center" id="sign_up" type="button" value="Sign Up" />
