                  {ReactHtmlParser (blockOne)}
                </div>
                <div class="col-sm-6 col-md-4">
                  <h5>{this.state.footer.data.entry.footer_section.footer_menu[1].title}</h5>{" "}
                 {ReactHtmlParser (blockTwo)}
                </div>
                <div class="col-sm-6 col-md-4">
                  <h5>{this.state.footer.data.entry.footer_section.footer_menu[2].title}</h5>{" "}

                </div>
              </div>
            </div>
          </div>
          <div class="footer-copyright">
            <span>
              <p>
                <a href="mailto:info@marketing.com">info@marketing.com </a>|
                Copyright Â© 2007-2016 Built.io. All Rights Reserved.
              </p>
            </span>
          </div>
        </footer>
      );
