import React, { Component } from 'react'
import dataSource from './DataSource'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import BlogContent from './BlogContent'
import "./css/style.css";
import "./css/blog.css";
import $ from 'jquery'
import { getData } from './Helper'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import Footer from './Footer'


class Blog extends Component {

    constructor(props) {
        super(props)

        this.state = {
            blogPage: "",
            image: ""
        }
    }


    componentDidMount() {

        getData(process.env.REACT_APP_URL_BLOG)
            .then((data) => {
                // console.log(data);
                this.setState({
                    blogPage: data
                }, () => {
                    console.log(this.state.blogPage)

                })

            }).catch((err) => {
                console.log(err);
            })
        getData(process.env.REACT_APP_URL_BLOG_IMAGE)
            .then((data) => {
                this.setState({
                    image: data
                }, () => { console.log(this.state.image) })
            }).catch((err) => {
                console.log(err);
            })

        var $header = $('.header-animated');
        var $headers = $('.header-animated opaque');
        var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
        var $logoDefault = $header.find('.logo > img').data('logo-default'); // black logo
        $(".header").css("display", "block")

        if (window.location.pathname == "/blog" || window.location.pathname == "/features" || window.location.pathname == "/about") {
            $(".header").addClass("opaque");
            $header.find('.logo > img').attr('src', $logoDefault);

        }
        else {
            $(".header").removeClass("opaque")
        }



    }

    render() {
        // console.log("stepone");
        console.log(this.props);

        if (this.state.blogPage !== "") {
            return (
                <>

                    <div class="main-container">
                        <section class="team-wrap">
                            <div class="container">
                                <div class="row" style={{ "margin-top": "70px", "margin-bottom":"40px" }}>
                                    <div class="col-md-4 margin-top-33 margin-bottom">
                                        <h1 id="welcome_header">WELCOME TO BLOG PAGE</h1>
                                        <hr />
                                        < div >
                                            {
                                                this.state.blogPage.data.entries.map((value) => {
                                                    // let blog_content = value.blog_content

                                                    return (
                                                        <>
                                                            <h1 id="blog_title" onClick={() => this.props.history.push(`/blog/${value.url.split("/")[1]}`, value)}><span>{value.title}</span></h1>
                                                            <p class="content_style">{value.blog_content}</p> <span onClick={() => this.props.history.push(`/blog/${value.url.split("/")[1]}`, value)}>Read More...</span>
                                                        </>
                                                    )
                                                })
                                            }
                                        </div>

                                    </div>
                                    {this.state.image !== "" && <div class="col-md-8" className="" style={{"top":"100px"}}>
                                        <img className="img-responsive" src={this.state.image.data.entry.file.url} alt="" />

                                    </div>}

                                </div>
                            </div>
                        </section>
                        <div class="card">
                            {
                                this.state.image !== "" &&

                                <><h3>{this.state.image.data.entry.blog_footer_section}</h3>
                                    <p>{this.state.image.data.entry.blog_footer_description}</p></>
                            }
                        </div>
                    </div>


                    <Footer />
                </>

            )
        }
        else if (this.state.blogPage === "") {
            return (
                null
            )
        }
    }
}


export default Blog




