import axios from 'axios'

export function getData(){
    const header = axios.create({
          baseURL:
          `${process.env.REACT_APP_URL_HEADER}`,
          headers: {
            api_key: process.env.REACT_APP_API_KEY,
            authtoken: process.env.REACT_APP_AUTHTOKEN
          }
        });

    const home = axios.create({
        baseURL:
        `${process.env.REACT_APP_URL_HOME}`,
        headers: {
          api_key: process.env.REACT_APP_API_KEY,
          authtoken: process.env.REACT_APP_AUTHTOKEN
        }
      });

}

export function getFeatures(){
    const features = axios.create({
        baseURL:
        `${process.env.REACT_APP_URL_FEATURES}`,
        headers: {
          api_key: process.env.REACT_APP_API_KEY,
          authtoken: process.env.REACT_APP_AUTHTOKEN
        }
      });
      return features.get()
}

export function getFooter(){
    const footer = axios.create({
        baseURL:
        `${process.env.REACT_APP_URL_FOOTER}`,
        headers: {
          api_key: process.env.REACT_APP_API_KEY,
          authtoken: process.env.REACT_APP_AUTHTOKEN
        }
      });
      return footer.get()
}

export function getProduct(){
    const product = axios.create({
        baseURL:
        `${process.env.REACT_APP_URL_PRODUCT}`,
        headers: {
          api_key: process.env.REACT_APP_API_KEY,
          authtoken: process.env.REACT_APP_AUTHTOKEN
        }
      });
      return product.get()
}

export function getAbout(){
    const about = axios.create({
        baseURL:
        `${process.env.REACT_APP_URL_ABOUT}`,
        headers: {
          api_key: process.env.REACT_APP_API_KEY,
          authtoken: process.env.REACT_APP_AUTHTOKEN
        }
      });
      return about.get()
}
