import React, { Component } from 'react'
import dataSource from './DataSource'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import BlogContent from './BlogContent'
import "./css/style.css";
import $ from 'jquery'
import { getData } from './Helper'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import Footer from './Footer'


class Blog extends Component {

    constructor(props) {
        super(props)

        this.state = {
            blogPage: "",
            image:""
        }
    }


    componentDidMount() {

        getData(process.env.REACT_APP_URL_BLOG)
            .then((data) => {
                // console.log(data);
                this.setState({
                    blogPage: data
                }, () => {
                    console.log(this.state.blogPage)

                })

            }).catch((err) => {
                console.log(err);
            })
        getData(process.env.REACT_APP_URL_BLOG_IMAGE)
        .then((data)=>{
            this.setState({
                image:data
            })
        }).catch((err)=>{
            console.log(err);
        })    

        var $header = $('.header-animated');
        var $headers = $('.header-animated opaque');
        var $logoAlt = $header.find('.logo > img').data('logo-alt'); // white logo
        var $logoDefault = $header.find('.logo > img').data('logo-default'); // black logo
        $(".header").css("display", "block")

        if (window.location.pathname == "/blog") {
            $(".header").addClass("opaque");
            $header.find('.logo > img').attr('src', $logoDefault);

        }


    }

    render() {
        console.log("stepone");
        console.log(this.props);

        if (this.state.blogPage !== "") {
            return (
                <>

                    <div class="main-container">
                        <section class="team-wrap">
                            <div class="container">
                                <div class="row">
                                    <div class="col-md-4 margin-top-33 margin-bottom">   
                                    <h1 id="welcome_header">Welcome To Blog Page</h1>  
                                    <hr/>                                 
                                            < div >  
                                                {
                                                    this.state.blogPage.data.entries.map((value) => {
                                                        let blog_content = value.blog_content
                                                        return (
                                                            <>
                                                               <h1 onClick={()=>this.props.history.push(`/blog/${value.url.split("/")[1]}`,value)}>{value.title}</h1>
                                                               {ReactHtmlParser(blog_content)}
                                                            </>
                                                        )
                                                    })
                                                }
                                            </div>                                        

                                    </div>
                                    <div class="col-md-8">
                                        <img class="img-responsive" src="https://stag-images.contentstack.io/v3/assets/blt74a459c57a383bbf/blt3020feb1aca1fcd3/5e0048609d54550df118633b/our-team.png" alt=""/>

                                    </div>

                                </div>
                            </div>
                        </section>


                    </div>

                    <Footer />
                </>

            )
        }
        else if (this.state.blogPage === "") {
            return (
                null
            )
        }
    }
}


export default Blog




